sys_ui_policy.script_true:
var PREPARATION = '7205a19897442110a4deb9dfe153af60'; // 10
var ELIGIBILITY = '0c55a1d497442110a4deb9dfe153af5f'; //20
var PRESELECTION_CBT = '5165ad5897442110a4deb9dfe153af6f'; //30
var PRESELECTION = '7f65a1d497442110a4deb9dfe153af65'; //40
var SELECTION = '8085299897442110a4deb9dfe153af4c'; //50
var RESERVE_LIST = '0c95259897442110a4deb9dfe153afb8'; // 60

function onCondition() {
    var state = g_form.getValue('state');
    var statesToSkip = g_form.getValue('states_to_skip');

    var hideEligibility = (statesToSkip.indexOf(ELIGIBILITY) > -1 ? true : false);
    var hideCBT = (statesToSkip.indexOf(PRESELECTION_CBT) > -1 ? true : false);
    var hidePreSelection = (statesToSkip.indexOf(PRESELECTION) > -1 ? true : false);
    var hideSelection = (statesToSkip.indexOf(SELECTION) > -1 ? true : false);

    if (hideEligibility) $j(".disabled")[0].hide();
    if (hideCBT) $j(".disabled")[1].hide();
    if (hidePreSelection) $j(".disabled")[2].hide();
    if (hideSelection) $j(".disabled")[3].hide();
}
